mapper.update:
UPDATE game
SET
	player_cnt = #{player_cnt}
WHERE
	game_idx=#{game_idx}

UPDATE game
SET
	now_player_cnt = now_player_cnt+#{now_player_cnt}
WHERE 
	game_idx=#{game_idx}

mapper.select:
SELECT * FROM game as g INNER JOIN user as u ON g.user_idx = u.user_idx INNER JOIN stadium as s ON g.stadium_idx = s.stadium_idx
AND g.user_idx IN
#{item}
AND g.del_ny='n' AND start_time_mill>#{ntm}
ORDER BY g.game_idx DESC

SELECT * FROM game as g INNER JOIN user as u ON g.user_idx = u.user_idx INNER JOIN stadium as s ON g.stadium_idx = s.stadium_idx
AND g.stadium_idx IN
#{item}
AND g.gender IN
#{item}
AND g.start_hr IN
#{item}
AND g.weekday IN
#{item}
AND g.del_ny='n'
ORDER BY g.game_idx DESC LIMIT #{s},#{c}
