mapper.select:
SELECT * FROM review AS r
INNER JOIN user AS u
ON r.wr_user_idx= u.user_idx
WHERE r.rv_user_idx=#{user_idx}
ORDER BY r.rv_idx DESC
LIMIT #{start}, #{cnt}

