mapper.update:
UPDATE user
SET
	review_cnt=review_cnt+1,
	star_rank=#{star_rank}
WHERE
	user_idx=#{user_idx}

UPDATE user
SET 
	join_cnt=join_cnt+#{join_cnt}
WHERE 
	user_idx=#{user_idx}

UPDATE user
SET 
	make_cnt=make_cnt+#{make_cnt}
WHERE 
	user_idx=#{user_idx}

mapper.select:

SELECT * 
FROM `user`
WHERE 
	id=#{id} 
	AND 
	login_type=#{login_type}

mapper.update:
UPDATE `user`
SET 
	image_url=#{image_url}, 
	small_image_url=#{small_image_url}
WHERE user_idx=#{user_idx}
